body.initApiAdaptor:
var data = payload.data;
if (data.tab_items && !_.isArray(data.tab_items)) {
    // 数据库中存储的是对象格式而不是数组，转换为数组用于界面显示
    let arrTabItems = [];
    _.each(data.tab_items, function (n, k) {
        n.tab_name = k;
        delete n.index;
        arrTabItems.push(n);
    });
    data.tab_items = arrTabItems;
}
if (data._id && !data.tab_items) {
    const tabOptions = data.tabs_options || [];
    const tabItems = [];
    // 把应用中原来tabs属性值添加到新的tab_items属性的默认值中
    if (data.tabs && data.tabs.length) {
        data.tabs.forEach(function (item) {
            tabItems.push({ tab_name: item });
        });
    }
    const pushObjectsToTabItems = function (objects) {
        objects.forEach(function (item) {
            // 已经有绑定到指定对象的选项卡就不添加
            let existObjectItem = !!tabItems.find(function (tabItem) {
                return !!tabOptions.find(function (option) {
                    return tabItem.tab_name === tabItem.value && option.object === item;
                });
            });
            if (existObjectItem) {
                return;
            }
            // 找到指向指定对象的选项卡
            let tempOption = tabOptions.find(function (option) {
                return option.type === "object" && option.object === item;
            });
            if (!tempOption) {
                return;
            }
            // 选项卡名称如果重复了就不添加
            let existTabItem = !!tabItems.find(function (tabItem) {
                return tabItem.tab_name === tempOption.value;
            });
            if (existTabItem) {
                return;
            }
            if (tempOption) {
                tabItems.push({ tab_name: tempOption.value });
            }
        });
    }
    // 把应用中原来objects属性值添加到新的tab_items属性的默认值中
    if (data.objects && data.objects.length) {
        pushObjectsToTabItems(data.objects);
    }
    // 把应用中原来mobile_objects属性值添加到新的tab_items属性的默认值中
    if (data.mobile_objects && data.mobile_objects.length) {
        pushObjectsToTabItems(data.mobile_objects);
    }
    data.tab_items = tabItems;
}

payload.data = data;
delete payload.extensions;
return payload;

body.apiRequestAdaptor:
delete formData.tabs_options;
const recordId = formData.recordId;

// 新加字段tab_items，值同步回传到老字段tabs中兼容老UI界面
formData.tabs = [];
if (formData.tab_items && formData.tab_items.length) { 
    formData.tabs = formData.tab_items.map(function (item) { return item.tab_name; });
}

/*
tab_items存储为：
tab_items: {
  test1:{
    group:"xxx"
  },
  test3:{
    group:"yyy"
  }
}
而不是：
tab_items: [
  {
    "index":1,
    "group":"xxx"
  },
  {
    "index":2,
    "group":"yyy"
  }
]
*/
if (formData.tab_items) { 
    formData.tab_items = _.keyBy(formData.tab_items, "tab_name")
    let tempIndex = 0;
    _.each(formData.tab_items, function (n, k) {
      tempIndex++;
      n.index = tempIndex;
      delete n.tab_name;
    });
}

