const fieldGroupKey = "group";
const getFieldsSortFun = function (groupNames) {
  return function (m, n) {
    var tempM = !!m[fieldGroupKey] ? 1 : 0;
    var tempN = !!n[fieldGroupKey] ? 1 : 0;
    if (!m[fieldGroupKey] || !n[fieldGroupKey]) {
      // 没有配置group属性的字段排在前面
      return tempM - tempN;
    }
    // 字段所属分组不在groupNames时排在前面，其他字段按其所属分组在groupNames中的排序先后顺序排序，所属分组相同的字段排在一起。
