  if (typeof pickedRelateds === "string") {
    pickedRelateds = pickedRelateds.split(",");
  }
  const convertedRelateds = pickedRelateds.map(function (key) {
    return superRelateds[key] || {
      related_field_fullname: key,
      sort_order: "asc"
    }
  });
  return {
    related_lists: convertedRelateds
  }
}

