actions.script:
//整理分组数据
let setting_groups = _.cloneDeep(event.data.groups);
if (!_.find(setting_groups, { is_default: true })) {
  setting_groups.unshift({
    "id": "未分组",
    "group_name": "未分组",
    "visible_on": "",
    "is_default": true
  })
}
if (!_.find(setting_groups, { is_hidden: true })) {
  setting_groups.push({
    id: "隐藏",
    group_name: "隐藏",
    visible_on: "",
    is_hidden: true
  });
}
setting_groups.forEach(function (group) {
  if (group.is_hidden) {
    group.group_name = "隐藏";
    group.visible_on = "";
  }
  if (group.is_default) {
    group.group_name = "未分组";
    group.visible_on = "";
  }
  if (!group.id) {
    group.id = group.group_name;
  }
  if (!group.visible_on) {
    group.visible_on = ""
  }
})

//整理字段与分组关系的数据
let fieldForGroup = {};
let oldGroup = _.cloneDeep(event.data.fieldForGroup);
setting_groups.forEach(function (group) {
  if (_.has(oldGroup, group.id)) {
    fieldForGroup[group.id] = oldGroup[group.id];
    oldGroup = _.omit(oldGroup, group.id);
  } else {
    fieldForGroup[group.id] = [];
  }
})
if (oldGroup && !_.isEmpty(oldGroup)) {
  _.forEach(oldGroup, function (value, key) {
    fieldForGroup["未分组"] = _.unionBy(fieldForGroup["未分组"], value);
  });
}

//未分组 放在所有分组开头，隐藏 放在所有分组最后
const defaultGroup = fieldForGroup["未分组"];
const hiddenGroup = fieldForGroup["隐藏"];
delete fieldForGroup["未分组"];
delete fieldForGroup["隐藏"];
fieldForGroup = _.merge({ "未分组": defaultGroup }, fieldForGroup, { "隐藏": hiddenGroup });

//根据fieldForGroup调整groups顺序，设置分组与保存时需要groups按照顺序
const keys = _.keys(fieldForGroup);
setting_groups = _.sortBy(setting_groups, function (group) { return _.findIndex(keys, function (key) { return key == group.group_name }) });

doAction({
  actionType: 'setValue',
  componentId: 'service_field_design',
  args: {
    value: {
      groups: setting_groups
    }
  }
});

doAction({
  actionType: 'setValue',
  componentId: "form_field_design",
  args: {
    value: { design_field: fieldForGroup }
  }
});

const newGroups = _.keys(event.data.value);
const lastGroups = _.map(context.props.data.groups, 'group_name');
if (newGroups != lastGroups) {
  doAction({
    actionType: 'setValue',
    componentId: 'service_field_design',
    args: {
      value: {
        fieldForGroup: event.data.value, groups: field_groups = _.sortBy(context.props.data.groups, function (group) { return _.findIndex(newGroups, function (group_name) { return group.group_name === group_name; }); })
      }
    }
  });
}

api.requestAdaptor:
var graphqlOrder = "";
//先修改字段，使系统对象自行自定义
//根据design_field,修改对象字段的sort_no与groups
var index = 1;
_.forEach(api.data.design_field, function (items, key) {
  let group = key;
  if (key == "未分组" || key == "隐藏") {
    group = null;
  }
  _.forEach(items, function (item) {
    const field = _.find(api.data.fields, { 'id': item });
    //如果是在隐藏分组里的字段，就给visible_on属性赋予{{false}};如果不在隐藏分组里，就判断来自数据库内的visible_on属性是不是{{false}},是就清除，不是就保持不变
    const visible_on = key == "隐藏" ? "{{false}}" : (field.attr_visible_on == "{{false}}" ? "{{true}}" : field.attr_visible_on);
    let docJson = {
      sort_no: index * 10,
      group, visible_on
    }
    if (field.id == field._id && field.is_system) {
      docJson = {
        object: api.data.$self.designObjectName,
        type: field.type,
        _name: field._name,
        label: field.label,
        sort_no: index * 10,
        group,
        visible_on,
        is_system: field.is_system
      }
    }
    if (key != "隐藏") {
      docJson.hidden = false;
    }
    let itemOrder = 'upsert' + index + ':object_fields__upsert(id:"' + item + '" , doc:' + JSON.stringify(JSON.stringify(docJson)) + '){_id}\n';
    graphqlOrder += itemOrder;
    index++;
  })
})

//根据groups，修改对象的字段分组field_groups
var field_groups = _.cloneDeep(api.data.groups);
_.remove(field_groups, { is_default: true });
_.remove(field_groups, { is_hidden: true });
field_groups = field_groups.map(function (group) {
  return _.omit(group, 'id');
})
const keys = _.keys(api.data.design_field);
field_groups = _.sortBy(field_groups, function (group) { return _.findIndex(keys, function (key) { return key == group.group_name }) });
const groupOrder = 'upsert0:objects__upsert(id:"' + api.data.$self.designObjectId + '" , doc:' + JSON.stringify(JSON.stringify({ field_groups, name: api.data.$self.designObjectName })) + ') {_id}';
graphqlOrder += groupOrder;


graphqlOrder = 'mutation {' + graphqlOrder + '}';
return {
  ...api,
  data: {
    query: graphqlOrder
  }
}

api.adaptor:
if (payload.errors) {
  payload.status = 2;
  payload.msg = window.t ? window.t(payload.errors[
    0
  ].message) : payload.errors[
    0
  ].message;
}
return payload;

//筛选出可编辑的字段
var oldFields = _.filter(payload.data.fields, function (obj) { return obj.recordPermissions.allowEdit; });
//将group为空的字段分为未分组的组中,将系统字段的_id改为对象名.字段名
oldFields = _.map(oldFields, function (obj) {
  obj.originId = obj._id;
  if (obj.hidden || obj.visible_on == "{{false}}") {
    obj.group = "隐藏";
  }else if (obj.group === null) {
    obj.group = "未分组";
  }
  if (obj.is_system) {
    obj._id = api.data.designObjectName + "." + obj._name;
  }
  return obj;
});

//整合出字段的属性集合
const fields = oldFields.map((field) => {
  return {
    "id": field._id,//用与steedos-board组件的关系分辨
    "_name": field._name,
    "label": field.label,
    "_id": field.originId,//用于打开steedos-objectform
    "columnSpan": field.is_wide ? 2 : 1,
    "is_system": field.is_system,
    "type": field.type,
    "attr_visible_on": field.visible_on
  }
})

//从字段的group属性中，整合出字段分组的属性集合
let field_groups = _.map(_.uniqBy(oldFields, "group"), function (obj) {
  return {
    "id": obj.group,
    "group_name": obj.group,
    "visible_on": ""
  };
});

//合并对象上的field_groups与字段上的group
let groups = _.unionBy(api.data.field_groups, field_groups, 'id');

//判断是否第一次调用接口，不是的话（说明是触发了datachange事件），返回fields与fieldUpdateData
if (api.data.dataInitialed) {
  const eventData = api.data.eventData;
  let fieldUpdateData = "";
  const fieldForGroup = api.data.fieldForGroup;
  let fieldForGroupLength = 0;
  _.forIn(fieldForGroup, function (value, key) {
    fieldForGroupLength += value.length;
  });
  if (fieldForGroupLength < fields.length) {
    fieldUpdateData = {
      type: "insert",
      id: eventData.result.data.recordId
    }
  } else if (fieldForGroupLength > fields.length) {
    fieldUpdateData = {
      type: "delete",
      id: eventData._id
    }
  }
  return payload = {
    data: {
      fields,
      fieldUpdateData
    }
  }
}

//整合字段与分组的关系
const oldFieldsInGroups = _.groupBy(oldFields, "group");
let fieldForGroup = _.mapValues(_.groupBy(groups, "id"), function (group,key) {
  if (oldFieldsInGroups[key]) {
    return _.map(oldFieldsInGroups[key], function (obj) {
      return obj._id;
    });
  } else {
    return [];
  }
});

//未分组 放在所有分组开头，隐藏 放在所有分组最后
const defaultGroup = fieldForGroup["未分组"];
const hiddenGroup = fieldForGroup["隐藏"];
delete fieldForGroup["未分组"];
delete fieldForGroup["隐藏"];
fieldForGroup = _.merge({ "未分组": defaultGroup }, fieldForGroup, { "隐藏": hiddenGroup });

//根据fieldForGroup调整groups顺序，设置分组与保存时需要groups按照顺序
const keys = _.keys(fieldForGroup);
groups = _.sortBy(groups, function (group) { return _.findIndex(keys, function (key) { return key == group.group_name }) });

return payload = {
  data: {
    fields,
    groups,
    fieldForGroup,
    dataInitialed: true
  }
};

actions.script:
const fieldForGroup = context.props.data.fieldForGroup;
const fieldUpdateData = event.data.fieldUpdateData;
if (fieldUpdateData && fieldUpdateData.type == "delete") {
  _.forIn(fieldForGroup, function (group, group_name) {
    _.remove(group, function (field) {
      return field === fieldUpdateData.id;
    })
  });
} else if (fieldUpdateData && fieldUpdateData.type == "insert") {
  fieldForGroup["未分组"].push(fieldUpdateData.id);
}
