schemaApi.adaptor:
const fields = context.uiSchema.fields;
const titleField = fields && fields.title;
const bodyField = fields && fields.body;
const variables = SteedosUI.getFormulaVariables(payload.data.options);
payload.data = [{
  label: titleField.label,
  type: 'input-formula',
  name: 'title',
  evalMode: false,
  variableMode: 'tabs',
  variables: variables,
  labelRemark: titleField.inlineHelpText,
  description: titleField.description
}, {
  label: bodyField.label,
  type: 'input-formula',
  name: 'body',
  evalMode: false,
  variableMode: 'tabs',
  variables: variables,
  labelRemark: bodyField.inlineHelpText,
  description: bodyField.description
}]
return payload;
